sys_script_client.script:
function onLoad() {
    var sections = g_form.getSections();
    for (var i = 0; i < sections.length; i++) {
        if (g_user.hasRole("x_1107692_banking.AccountManager")) {
            g_form.setSectionDisplay("credit_verification", true);
            g_form.setSectionDisplay("document_verification", true);
            g_form.setSectionDisplay("loan_amount_verification", true);
        } else if (g_user.hasRole("x_1107692_banking.AccountUser")) {
            g_form.setSectionDisplay("document_verification", false);
            g_form.setSectionDisplay("amount_verification", false);
        } else if (g_user.hasRole("x_1107692_banking.GovernmentUser")) {
            g_form.setSectionDisplay("amount_verification", false);
            g_form.setSectionDisplay("credit_verification", false);
        } else if (g_user.hasRole("x_1107692_banking.FinanceUser")) {
            g_form.setSectionDisplay("document_verification", false);
            g_form.setSectionDisplay("credit_verification", false);
        }
    }
}
