sys_script_client.script:
function onLoad() {
    var sections = g_form.getSections();
    for (var i = 0; i < sections.length; i++) {
        if (g_user.hasRole("x_1107692_banking.AccountManager")) {
            g_form.setSectionDisplay("government_proofs", true);
            g_form.setSectionDisplay("finance", true);
            g_form.setSectionDisplay("personal_details", true);
        } else if (g_user.hasRole("x_1107692_banking.AccountUser")) {
            g_form.setSectionDisplay("government_details", false);
            g_form.setSectionDisplay("finance_details", false);
        } else if (g_user.hasRole("x_1107692_banking.GovernmentUser")) {
            g_form.setSectionDisplay("finance_details", false);
            g_form.setSectionDisplay("personal_details", false);
        } else if (g_user.hasRole("x_1107692_banking.FinanceUser")) {
            g_form.setSectionDisplay("government_details", false);
            g_form.setSectionDisplay("personal_details", false);
        }
    }
}
