var BankingApplicationUtil = Class.create();
BankingApplicationUtil.prototype = {
    initialize: function() {},
    uiActionVisibility: function(current) {
        if((gs.hasRole('x_1107692_banking.AccountUser') && (current.personal_detail_approval == '' || current.personal_detail_approval == 'NO')) || (gs.hasRole('x_1107692_banking.GovernmentUser') && (current.govt_details_approval == '' || current.govt_details_approval == 'NO')) || (gs.hasRole('x_1107692_banking.FinanceUser') && (current.finance_approval_status == '' || current.finance_approval_status == 'NO'))){
      return true;
		}

    },

    type: 'BankingApplicationUtil'
};
