sys_ui_action.script:
function trigerAccountVerificationflow() {

    try {
        var inputs = {};
        var accountflowinputs = {};
        var grouprec;
        if (gs.hasRole('x_1107692_banking.AccountUser')) {
            grouprec = new GlideRecord('sys_user_group');
            grouprec.get('225904c9839092502740f496feaad397');
            inputs['approver_group'] = grouprec; // GlideRecord of table: sys_user_group 
            inputs['approval_for'] = current; // GlideRecord of table: x_1107692_banking_accountant in
            inputs['requesting_details'] = 'Personal Details Tab';
            inputs['fieldnameforapproval'] = 'personal_detail_approval';
            inputs['fieldnameforverification'] = 'personal_verification';
        } else if (gs.hasRole('x_1107692_banking.GovernmentUser')) {
            grouprec = new GlideRecord('sys_user_group');
            grouprec.get('64894cc9839092502740f496feaad3dc');
            inputs['approver_group'] = grouprec; // GlideRecord of table: sys_user_group 
            inputs['approval_for'] = current; // GlideRecord of table: x_1107692_banking_accountant in
            inputs['requesting_details'] = 'Government Proofs Details Tab';
            inputs['fieldnameforapproval'] = 'govt_details_approval';
            inputs['fieldnameforverification'] = 'goverment_verification';

        } else if (gs.hasRole('x_1107692_banking.FinanceUser')) {
            grouprec = new GlideRecord('sys_user_group');
            grouprec.get('138900c9839092502740f496feaad345');
            inputs['approver_group'] = grouprec; // GlideRecord of table: sys_user_group 
            inputs['approval_for'] = current; // GlideRecord of table: x_1107692_banking_accountant in
            inputs['requesting_details'] = 'Finance Details Tab';
            inputs['fieldnameforapproval'] = 'finance_approval_status';
            inputs['fieldnameforverification'] = 'finance_verification_status';
        }
        sn_fd.FlowAPI.getRunner().subflow('x_1107692_banking.approvals_and_task_generation').inBackground().withInputs(inputs).run();

        if (current.account_verification == '3' || current.account_verification == '4') {
            try {
                accountflowinputs['account_record'] = current;
                var result = sn_fd.FlowAPI.getRunner().subflow('x_1107692_banking.account_verification_for_banking_account').inBackground().withInputs(accountflowinputs).run();
            } catch (ex) {
                var message1 = ex.getMessage();
                gs.error(message1);
            }
        }
    } catch (ex) {
        var message = ex.getMessage();
        gs.error(message);
    }
    action.setRedirectURL("/x_1107692_banking_accountant.do?sys_id=" + current.sys_id);
}

trigerAccountVerificationflow();
