sys_ui_action.script:
function trigerAccountVerificationflow() {
    try {
        //this will give you array of changed field names
        var changedfields = getChangedFieldNames(current);
        if (changedfields.indexOf('u_string_13')>=0 && (current.loan_process_request == 'active')) {
            current.loan_request_verification = 'new';
            current.loan_process_approval = 'High YES';
            current.state = '65';
        } else if (current.state == '3' && (current.loan_process_request == 'active' || current.loan_process_request == 'terminated')) {
            current.loan_request_verification = 'new';
            current.loan_process_approval = 'YES';
            current.state = '45';
        } else {
            if (gs.hasRole('x_1107692_banking.AccountManager')) {
                current.u_choice_1 = 'completed';
                current.u_string_3 = 'NO';
                current.u_choice_2 = 'completed';
                current.u_string_4 = 'NO';
                current.u_choice_6 = 'completed';
                current.u_string_7 = 'NO';

            } else {
                if (gs.hasRole('x_1107692_banking.AccountUser')) {
                    current.u_choice_1 = 'new';
                    current.u_string_3 = 'YES';

                } else if (gs.hasRole('x_1107692_banking.GovernmentUser')) {
                    current.u_choice_2 = 'new';
                    current.u_string_4 = 'YES';
                } else if (gs.hasRole('x_1107692_banking.FinanceUser')) {
                    current.u_choice_6 = 'new';
                    current.u_string_7 = 'YES';
                }
            }
            current.state = '2';
        }
        current.active = true;
        current.update();
    } catch (ex) {
        var message = ex.getMessage();
        gs.error(message);
    }
    action.setRedirectURL("/x_1107692_banking_ticket.do?sys_id=" + current.sys_id);
}

trigerAccountVerificationflow();

function getChangedFieldNames(gr) {

    var result = [];
    var elements = gr.getElements();
    var size = elements.length;
    for (var i = 0; i < size; i++) {
        var ge = elements[i];
        if (ge.changes()) {
            result.push(ge.getName());
        }
    }

    return result;
}
