sysrule_view.script:
(function overrideView(view, is_list) {

    // Add your code here
    answer = null; // set the new view to answer

    var currentUserID = gs.getUserID();

    var userRoles = new GlideRecord('sys_user_has_role');
    userRoles.addQuery('user', currentUserID);
    userRoles.addQuery('role', '2cee036c83589a102740f496feaad393').addOrCondition('role',"57ce0b2c83589a102740f496feaad3be").addOrCondition('role','7ede0ba883589a102740f496feaad384');
    userRoles.query();
    if (userRoles.next()) {
        if (userRoles.role == "2cee036c83589a102740f496feaad393") {
            answer = 'Customer_view';
        } else if (userRoles.role == "57ce0b2c83589a102740f496feaad3be") {
            answer = 'Manager_View';
        } else
            answer = "Accountant_View";
    }
})(view, is_list);
